import React from 'react';
import { useState } from 'react';
import {Link} from 'react-scroll'; 
import b4la from '../Images/B4LA.mp4'; 
import group from '../Images/group2.png';
import baum from '../Images/baum.png';
import wires from '../Images/wires.png';
import fight from '../Images/fight.png';
import repair from '../Images/repair.png'; 


const Gallery = () => {
    const slides = [
        {img: group, title: 'Competition'},
        {img: group, title: 'GroupPhoto'},
        {img: repair, title: 'Assembly'},
        {img: wires, title: 'Wiring'},
        {img: baum, title: 'Baum'},
        {img: fight, title: 'RobotsFighting'},
    ]; 

    const containerStyles = {
        width: "500px",
        height: "280px",
        margin: "0 auto",
    };

    const ImageSlider = ({slides}) => {
        const [currentIndex, setCurrentUser] = useState(0); 
        const sliderStyles = {
            height: '100%',
            position: 'relative',
        }
        const slidesStyles = {
            width: '100%',
            height: '100%',
            borderRadius: '10px',
            backgroudPosition: 'center',
            backgroundSize: "cover",
            backgroundImage: slides[currentIndex].img
        }; 

        return (
            <div style={sliderStyles}>
                <div style={{slidesStyles}}>

                </div>
            </div>
        );
    }; 

    return (
        <section id="gallery">
            <div style={containerStyles}>
                <ImageSlider slides={slides}></ImageSlider>
            </div>
            {/*<video width="750" height="500" controls >
            <source src={b4la} type="video/mp4"/>
            </video>*/}
        </section>
    ); 
}

export default Gallery;
