import React from 'react';
import { useState } from 'react';
import {Link} from 'react-scroll'; 
import './Projects.css'; 
import Queen from '../Images/ArcQ.png'; 
import Robot from '../Images/CAD2.png'; 

const Works = () => {
    const [hover, setHover] = useState(false); // initial false

    const HoverData = "Click or pinch to Zoom Image";

    const onHover = (e) => {
        e.preventDefault();
        setHover(true); // turn true
        console.log("hovered");
    };

    const onHoverOver = (e) => {
        e.preventDefault(); // turn false
        setHover(false);
    };

    return (
        <section id='works'>
            <h2 className="worksTitle">Our Projects</h2>
            <span className="worksDesc">The team has been working on two robots. We've designed and built Queen of Hearts (left), competing with it at a recent tournament in SoCal. 
            We've also been working on our second robot and are awaiting further funding to complete it. See more at our Instagram! </span>

            {hover && <div className="worksImgs">
                {/*hover && <p className={hover}>{HoverData}</p>*/}
                <img onMouseEnter={(e) => onHover(e)} onMouseLeave={(e) => onHoverOver(e)} 
                    src={Queen} alt="p1" className="worksImg"></img>
                <img src={Robot} alt="p2" className="worksImg"></img>
            </div>
            }
            {!hover && <div className="worksImgs">
                {/*hover && <p className={hover}>{HoverData}</p>*/}
                <img onMouseEnter={(e) => onHover(e)} onMouseLeave={(e) => onHoverOver(e)} 
                    src={Queen} alt="p1" className="worksImg"/>Hi
                <img src={Robot} alt="p2" className="worksImg"/>Hi
            </div>}


            <a href="https://www.instagram.com/usc.arc/" target="_blank" rel="noreferrer">
                <button className ="workBtn">Instagram</button>
            </a>
        </section>
    );
}

export default Works;
